.section {
  padding: 4.5rem 0 4rem;
}
.section__title {
  font-size: 2.5rem;
  color: var(--text);
  text-transform: uppercase;
  margin-bottom: 2rem;
  font-family: "Josefin Sans", sans-serif;
}

.container {
  max-width: 968px;
  margin-left: 1.5rem;
  margin-right: 1.5rem;
}

.grid {
  display: grid;
}

.btn {
  padding: 0.7rem 1.5rem;
  background: #ff0095;
  color: #ffebeb;
  text-align: center;
  cursor: pointer;
}

.toggle {
  position: fixed;
  top: 2.5rem;
  left: 2.5rem;
  width: 1.5rem;
  height: 1.5rem;
  background: white;
  z-index: 2;
}
.toggle::before {
  content: "";
  position: absolute;
  top: 0.4rem;
  left: 0.2rem;
  width: 1.1rem;
  height: 0.2rem;
  background: #171717;
  transition: transform 0.3s;
}
.toggle::after {
  content: "";
  position: absolute;
  bottom: 0.4rem;
  left: 0.2rem;
  width: 1.1rem;
  height: 0.2rem;
  background: #171717;
  transition: 0.3s;
}

.active.toggle::before {
  transform: rotate(45deg) translateY(0.35rem);
  left: 0.45rem;
}
.active.toggle::after {
  transform: rotate(135deg) translateY(0.35rem);
  left: 0.45rem;
}

.main {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: start;
  align-items: center;
  background: #0d0d0d right/cover no-repeat url(/030e286a9226bd425fad7a1e445dcdb7.jpg);
}
.main::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background: linear-gradient(90deg, #0d0d0d 30%, rgba(0, 0, 0, 0) 60%);
}
.main__content {
  z-index: 1;
  width: 50%;
  display: flex;
  flex-direction: column;
  gap: 1rem;
  margin-left: 2.5rem;
}
.main__content h1, .main__content h3, .main__content p {
  text-shadow: 2px 2px 2px #000;
}
.main__content .btn {
  min-width: 10rem;
  width: 40%;
}
.main__content .btn:hover {
  background: #ff33aa;
}
.main__title {
  font-size: 5rem;
  font-family: "Josefin Sans", sans-serif;
}

.menu {
  position: fixed;
  top: 0;
  left: 0;
  display: flex;
  flex-direction: column;
  gap: 5%;
  justify-content: center;
  align-items: end;
  opacity: 0;
  width: 100%;
  height: 100vh;
  background: #0d0d0d right/cover no-repeat url(/288384c8670f425462040e5fc93dba9a.jpg);
  transition: opacity 0.4s;
}
.menu__item {
  margin-right: 2.5rem;
  font-size: 1.5rem;
  position: relative;
  cursor: pointer;
  color: #ffebeb;
}
.menu__item::after {
  content: "";
  position: absolute;
  bottom: -0.3rem;
  right: 0;
  width: 0;
  height: 0.2rem;
  background: #ffebeb;
  transition: width 0.2s;
}
.menu__item:hover::after {
  width: 100%;
}

.show {
  opacity: 1;
  z-index: 1;
}

.modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background: rgba(0, 0, 0, 0.8);
  display: none;
  justify-content: center;
  align-items: center;
}
.modal .content {
  width: 90%;
