@import url('https://fonts.googleapis.com/css2?family=Poppins:ital,wght@0,100;0,200;0,300;0,400;0,500;1,100;1,200;1,300;1,400;1,500&family=Righteous&family=Josefin+Sans&display=swap');

// COLORS
$background: hsl(0, 0%, 5%);
$dark: hsl(0, 0%, 9%);
$accent: hsl(335, 100%, 50%);
$accent2: hsl(335, 100%, 60%);
$light: hsl(0, 100%, 96%);
$text: hsl(0, 100%, 96%);
$modal: hsla(0, 0%,0%, .8);

// SHADOWS
$textShadow: 1px 1px 10px #000;

sources.src/scss/base/_base.scss:
* {
  box-sizing: border-box;
  padding: 0;
  margin: 0;
}

html {
  scroll-behavior: smooth;
}

body {
  font-family: 'Poppins', sans-serif;
  color: $text;
}

ul {
  list-style: none;
}

a {
  text-decoration: none;
}

img {
  max-width: 100%;
  height: auto;
}

button,
input {
  border: none;
  outline: none;
}

button {
  cursor: pointer;
}

.p {
  &-1 { padding: 1rem ; }
  &-2 { padding: 2rem ; }
  &-3 { padding: 3rem ; }
  &-4 { padding: 4rem ; }
}

::-webkit-scrollbar {
  width: .5rem;

  &-track {
    background-color: $light;
    -webkit-border-radius: .5rem;
    border-radius: .5rem;
  }

  &-thumb {
    -webkit-border-radius: .2rem;
    border-radius: .2rem;
    background: hsl(0, 0%, 43%); 

    &:hover {
      background: hsl(0, 0%, 33%);
    }
  }
}

::-moz-selection { background-color: hsla(335, 100%, 50%, .5)}
::selection { background-color: hsla(335, 100%, 50%, .5)}

