sources.src/scss/components/_buttons.scss:
.btn {
  padding: .7rem 1.5rem;
  background: $accent;
  color: $text;
  text-align: center;
  cursor: pointer;

  &-watch { 
    display: flex;
    font-size: 1.1rem;
    justify-content: center;
    align-items: center;
    gap: .5rem;
  }
}

.toggle {
  position: fixed;
  top: 2.5rem;
  left: 2.5rem;
  width: 1.5rem;
  height: 1.5rem;
  background: white;
  z-index: 2;

  &::before {
    content: '';
    position: absolute;
    top: .4rem;
    left: .2rem;
    width: 1.1rem;
    height: .2rem;
    background: $dark;
    transition: transform .3s;
  }
  &::after {
    content: '';
    position: absolute;
    bottom: .4rem;
    left: .2rem;
    width: 1.1rem;
    height: .2rem;
    background: $dark;
    transition: .3s;
  }
}

.active.toggle {
  &::before {
    transform: rotate(45deg) translateY(.35rem);
    left: .45rem;
  }
  &::after {
    transform: rotate(135deg) translateY(.35rem);
    left: .45rem;
  }
}

.tooltip {
  padding: .1rem .7rem;
  position: fixed;
  top: 2.4rem;
  left: 5.3rem;
  background: $light;
  font-size: .9rem;
  opacity: 0;
  z-index: 2;
  color: $dark;
  border-radius: .5rem;
  animation: appear 4s 2s;

  &::before {
    content: '';
    position: absolute;
    top: .15rem;
    left: -.7rem;
    border-style: solid;
    border-width: .55rem 1rem .55rem 0;
    border-color: transparent $light transparent transparent;
  }
}

sources.src/scss/components/_main.scss:
.main {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: start;
  align-items: center;
  background: $background right / cover no-repeat url(./../../img/imgb-02.jpg);

  &::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100vh;
    background: linear-gradient(90deg, $background 30%, hsla(0,0%,0%,0) 50%);
  }

  &__content {
    position: relative;
    z-index: 1;
    width: 50%;
    display: flex;
    flex-direction: column;
    gap: 1rem;
    margin-left: 2.5rem;

    & h1, h3, p {
      text-shadow: $textShadow;
    }

    & .btn {
      min-width: 10rem;
      width: 40%;

      &:hover {
        background: $accent2;
      }
    }
  }

  &__title {
    font-size: 5rem;
    font-family: 'Josefin Sans', sans-serif;
    position: relative;

    &::after {
      content: '';
      position: absolute;
      top: -1.6rem;
      left: 14.1rem;
      height: .9rem;
      width: .9rem;
      border-radius: 50%;
      background: $text;
      animation: move .5s alternate infinite ease 2s;
    }

    &::first-letter {
      color: $accent;
    }
  }
  &__description {
    font-size: 1.3rem;
  }
}

.social {
  position: absolute;
  bottom: 2.5rem;
  left: 2.5rem;
  display: flex;
  gap: 1.2rem;
  animation: appear 1s ease 2s;

  & a {
    color: white;

    &:hover {
      color: $accent2;
    }
  }

  &-icon { 
    font-size: 1.5rem;
  }
}

.clip {
  z-index: 2;
  position: absolute;
  bottom: 3rem;
  right: 3rem;
  animation: appear 2s ease 2s;

  &-box { 
    border: .3rem solid $light;
    opacity: .7;

    &:hover {
      opacity: 1;
    }

  }
}

sources.src/scss/components/_menu.scss:
.menu {
  position: fixed;
  top: 0;
  left: 0;
  display: flex;
  flex-direction: column;
  gap: 5%;
  justify-content: center;
  align-items: end;
  opacity: 0;
  width: 100%;
  height: 100vh;
  background: $background right / cover no-repeat url(./../../img/menu.jpg);
  transition: opacity 0.4s;

  &__title {
    color: $text;
    position: absolute;
    font-size: 2rem;
    top: 1.75rem;
    left: 4.5rem;

    &::first-letter {
      color: $accent;
    }
  }

  &__item {
    margin-right: 2.5rem;
    font-size: 1.5rem;
    position: relative;
    cursor: pointer;
    color: $text;
    transition: color .4s;

    &:hover {
      color: $accent;
    }

    &::after {
      content: '';
      position: absolute;
      bottom: -.3rem;
      right: 0;
      width: 0;
      height: .2rem;
      background: $text;
      transition: width .2s, background .4s;
    }

    &:hover::after {
      width: 100%;
      background: $accent;
    }
  }
}

.show {
  opacity: 1;
  z-index: 1;
}

sources.src/scss/components/_modal.scss:
.modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background: $modal;
  display: none;
  justify-content: center;
  align-items: center;

  & .content {
    position: relative;
    width: 90%;
    max-width: 30rem;
    background: $light;
    padding: 4rem 2rem 2rem;
    color: $dark;
    overflow: auto;
    max-height: 70%;

    &::before {
      content: '';
      position: absolute;
      top: 1.8rem;
      right: 2rem;
      height: .2rem;
      width: 50%;
      background: $accent;
    }

    &__title { 
      font-size: 2rem;
      text-transform: uppercase;
      position: absolute;
      left: 2rem;
      top: .5rem;

      &::first-letter {
        color: $accent;
      }
    }
  }
}

.close {
  position: absolute;
  font-size: 4rem;
  color: $accent;
  text-shadow: $textShadow;
  top: .5rem;
  right: 1.5rem;
  z-index: 10;
  cursor: pointer;
}

sources.src/scss/components/_gallery.scss:
.gallery {
  position: fixed;
  width: 100%;
  height: 100vh;
  display: flex;

  & .cast {
    flex: 1;
    transition: all .4s ease;
    padding-bottom: 3rem;
    display: flex;
    flex-direction: column;
    justify-content: end;
    align-items: center;
    filter: grayscale(90%);

    &:hover {
      flex: 7;
      filter: none;
    }

    &:nth-child(1) { background: $background center / cover no-repeat url(./../../img/img-01.jpg); }
    &:nth-child(2) { background: $background center / cover no-repeat url(./../../img/img-02.jpg); }
    &:nth-child(3) { background: $background center / cover no-repeat url(./../../img/img-03.jpg); }
    &:nth-child(4) { background: $background center / cover no-repeat url(./../../img/img-04.jpg); }
    &:nth-child(5) { background: $background center / cover no-repeat url(./../../img/img-05.jpg); }
    &:nth-child(6) { background: $background center / cover no-repeat url(./../../img/img-06.jpg); }
    &:nth-child(7) { background: $background center / cover no-repeat url(./../../img/img-07.jpg); }
    &:nth-child(8) { 
      background: $background center / cover no-repeat url(./../../img/img-08.jpg); 
      flex-direction: column;
      justify-content: center;
      align-items: start;
      padding: 0 2rem;
    }

    &-char, &-actor {
      text-shadow: $textShadow;
      opacity: 0;
      transition: opacity .7s;
    }

    &-char { font-size: 3rem;  }

    &-actor { font-size: 1.3rem;} 

    &:hover .cast-char, &:hover .cast-actor {
      opacity: 1;
    }
  }
}

sources.src/scss/components/_keyframes.scss:
@keyframes blink {
  0% { opacity: 0; z-index: 0; }
  20% { opacity: 1; z-index: 2; }
  80% { opacity: 1; z-index: 2; }
  100% { opacity: 0; z-index: 0; }
}

@keyframes move {
  from { top: -1.6rem ;}
  to { top: -2.7rem ;}
}

sources.src/scss/components/_breakpoints.scss:
@media screen and (max-width: 650px ) {
  .main {
    padding-top: 100%;
    flex-direction: column;
    align-items: left;
    overflow: auto;

    &::before {
      bottom: 0;
      width: 100%;
      height: 100vh;
      background: linear-gradient(0deg, $background 20%, hsla(0,0%,0%,0) 60%);
    }

    &__content {
      width: 85%;
      gap: 1rem;
      margin-left: 0;
      flex-wrap: wrap;

      & .btn {
        width: 100%;
      }
    }

    &__sub { font-size: 1rem; }
    &__description { font-size: 1.2rem; }
    &__title { 
      font-size: 4rem; 

      &::after { 
        left: 11.2rem;
      }
    }
  }

  .social {
    position: relative;
    bottom: auto;
    left: initial;
    margin: 2rem 0;
  }

  .clip {
    position: relative;
    bottom: 0;
    margin-top: 1.5rem;
    margin-left: 2.5rem;
    height: 70%;
    width: 70%;
  }

  .gallery {
    flex-direction: column;

    & .cast {
      height: 10%;
      padding-bottom: 0;
      padding: 0 1rem;
      justify-content: center;
      align-items: start;

      &:hover {
        flex: 3;
      }

      &:nth-child(2),
      &:nth-child(3),
      &:nth-child(7) {
        align-items: end;
      }

      &-char { font-size: 1.8rem;  }
      &-actor { font-size: 1.1rem; } 
    }
  }

  .modal {
    & .content {
      &::before {
        width: 30%;
      }
    }
  }
}
